// SearchPage.js
import React, { useState, useEffect } from 'react';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [songResults, setSongResults] = useState([]);
  const [selectedSong, setSelectedSong] = useState(null);
  const [playlists, setPlaylists] = useState([]);
  const [showOptions, setShowOptions] = useState(false);

  useEffect(() => {
    const debounce = (func, delay) => {
      let timeoutId;
      return function (...args) {
        if (timeoutId) {
          clearTimeout(timeoutId);
        }
        timeoutId = setTimeout(() => {
          func(...args);
        }, delay);
      };
    };

    const debouncedSearch = debounce(async (term) => {
      if (term.trim() === '') {
        setSongResults([]);
        return;
      }

      try {
        const response = await fetch(`http://localhost:3002/api/search?q=${encodeURIComponent(term)}`);
        const data = await response.json();

        setSongResults(data.filter((item) => item.type === 'song'));
      } catch (error) {
        console.error(error);
      }
    }, 300);

    debouncedSearch(searchTerm);
  }, [searchTerm]);

  // Fetch the playlists from MongoDB when the component mounts
  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');
        console.log(response);
        const data = await response.json();
        setPlaylists(data.playlists);
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    fetchPlaylists();
  }, []);

  // Handler to show options for a selected song
  const handleShowOptions = (song) => {
    setSelectedSong(song);
    setShowOptions(true);
  };

  // Handler to close the options section
  const handleCloseOptions = () => {
    setSelectedSong(null);
    setShowOptions(false);
  };

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div>
        {/* Display song results */}
        {songResults.map((song) => (
          <div key={song.id}>
            <h3>{song.name}</h3>
            <p>{song.artist}</p>
            <button onClick={() => handleShowOptions(song)}>More Options</button>
            {showOptions && selectedSong && selectedSong.id === song.id && (
              <div>
                {/* Display options for the selected song */}
                <button onClick={() => console.log("Option 1: Display all playlists from MongoDB")}>Option 1</button>
                <button onClick={() => console.log("Option 2")}>Option 2</button>
                <button onClick={() => console.log("Option 3")}>Option 3</button>
                <button onClick={handleCloseOptions}>Close Options</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchPage;
