// SearchPage.js
import React, { useState } from 'react';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:3002/api/search?q=${encodeURIComponent(searchTerm)}`);
      const data = await response.json();
      console.log(data);
      setResults(data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
            {result.album}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SearchPage;
