import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

const PlaylistDetails = ({ setSelectedSong }) => {
  const { playlistId } = useParams();
  const history = useHistory();
  const [playlist, setPlaylist] = useState(null);

  useEffect(() => {
    const fetchPlaylistDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3002/api/playlists/${playlistId}`);
        console.log('Playlist Details Response:', response); 
        if (response.status === 200) {
          setPlaylist(response.data.playlist);
        }
      } catch (error) {
        console.error('An error occurred:', error);
      }
    };

    fetchPlaylistDetails();
  }, [playlistId]);

  const handleSongClick = (song) => {
    console.log(song);
    setSelectedSong(song);
  };

  const handleDeletePlaylist = async () => {
    try {
      const response = await axios.delete(`http://localhost:3002/api/playlists/${playlistId}`);
      if (response.status === 200) {
        console.log('Playlist deleted successfully.');
        // Redirect to the home page or any other page after deletion
        history.push('/');
      }
    } catch (error) {
      console.error('An error occurred while deleting the playlist:', error);
    }
  };

  return (
    <div className="playlist-details">
      <h2>Playlist Details</h2>
      {playlist ? (
        <div>
          <h3>{playlist.name}</h3>
          <p>Created on: {new Date(playlist.createdAt).toLocaleDateString()}</p>
          <p>Last Updated: {new Date(playlist.updatedAt).toLocaleDateString()}</p>
          <h4>Tracks:</h4>
          <ul>
            {playlist.tracks.map((track) => (
              <li key={track._id} onClick={() => setSelectedSong(track)}>
                <strong>Title:</strong> {track.name}, <strong>Artist:</strong> {track.artist}
              </li>
            ))}
          </ul>
          <button onClick={handleDeletePlaylist}>Delete Playlist</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default PlaylistDetails;
