// SongResults.js
import React, { useState, useEffect } from 'react';
import OptionsPopup from './OptionsPopup';
import './SongResults.css';

function SongResults({ results, playSong }) {
  const [selectedSong, setSelectedSong] = useState(null);
  const [playlists, setPlaylists] = useState([]);
  const [showPlaylists, setShowPlaylists] = useState(false);

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');
        if (response.ok) {
          const data = await response.json();
          setPlaylists(data.playlists);
        } else {
          console.error('Failed to fetch playlists:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    // Fetch playlists when the component mounts
    fetchPlaylists();
  }, []);

  const handleShowOptions = (result) => {
    setSelectedSong(result);
  };

  const handleCloseOptions = () => {
    setSelectedSong(null);
    setShowPlaylists(false); // Reset the state when closing options
  };

  const handleOption1 = (playlist) => {
    console.log('Playlist ID:', playlist._id);
    console.log('Selected Song ID:', selectedSong._id);
    setShowPlaylists(true);
    console.log("Option 1 - Display playlists");
  };

  const handleOption2 = () => {
    console.log("Option 2");
    handleCloseOptions();
  };

  const handleOption3 = () => {
    console.log("Option 3");
    handleCloseOptions();
  };

  const handlePlaylistClick = async (playlistId) => {
    try {
      const response = await fetch(`http://localhost:3002/api/playlists/${playlistId}/addSong`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ songId: selectedSong._id }),
      });

      if (response.ok) {
        console.log(`Song added to playlist ${playlistId}`);
      } else {
        console.error('Failed to add song to playlist:', response.status);
      }
    } catch (error) {
      console.error('An error occurred while adding song to playlist:', error);
    }
  };

  return (
    <div>
      <ul className="song-container">
        {results.map((result) => (
          <li key={result._id} className="song-item">
            <span className="play-button" onClick={() => playSong(result)}>
              ▶️
            </span>
            <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
            {result.album}

            {/* Replace the options button with three buttons */}
            <div className="options-container">
              <button onClick={() => handleOption1(result)}>Option 1</button>
              <button onClick={handleOption2}>Option 2</button>
              <button onClick={handleOption3}>Option 3</button>
            </div>

            {/* Options popup for the selected song */}
            {selectedSong && selectedSong._id === result._id && (
              <OptionsPopup
                playlists={playlists}
                showPlaylists={showPlaylists}
                handleOption1={handleOption1}
                handleOption2={handleOption2}
                handleOption3={handleOption3}
                handleCloseOptions={handleCloseOptions}
                handlePlaylistClick={handlePlaylistClick} 
                selectedSong={selectedSong}
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SongResults;
