// SongResults.js
import React, { useState, useEffect } from 'react';

function SongResults({ results }) {
  const [showOptions, setShowOptions] = useState(null);
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');
        if (response.ok) {
          const data = await response.json();
          setPlaylists(data.playlists);
        } else {
          console.error('Failed to fetch playlists:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    // Fetch playlists when the component mounts
    fetchPlaylists();
  }, []); // Use an empty dependency array to ensure useEffect runs only once on mount

  const handleShowOptions = (result) => {
    setShowOptions(result._id);
  };

  const handleCloseOptions = () => {
    setShowOptions(null);
  };

  const handleOption1 = () => {
    console.log("Option 1");
    // Implement your logic for Option 1 (already displaying playlists)
  };

  const handleOption2 = () => {
    console.log("Option 2");
    // Implement your logic for Option 2
  };

  const handleOption3 = () => {
    console.log("Option 3");
    // Implement your logic for Option 3
  };

  return (
    <div>
      <h2>Song Results</h2>
      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
            {result.album}
            <button onClick={() => handleShowOptions(result)}>More Options</button>

            {/* Options for the selected song */}
            {showOptions === result._id && (
              <div>
                <button onClick={handleOption1}>Option 1</button>
                <button onClick={handleOption2}>Option 2</button>
                <button onClick={handleOption3}>Option 3</button>
                <button onClick={handleCloseOptions}>Close Options</button>
              </div>
            )}
          </li>
        ))}
      </ul>

      {/* Display all playlists for Option 1 */}
      {showOptions && playlists.length > 0 && showOptions === results[0]._id && (
        <div>
          <h3>All Playlists</h3>
          <ul>
            {playlists.map((playlist) => (
              <li key={playlist.id}>{playlist.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SongResults;
