// Footer.js
import React, { useEffect, useState } from 'react';

function Footer({ selectedSong }) {
  const [audioUrl, setAudioUrl] = useState('');

  useEffect(() => {
    const fetchAudioUrl = async () => {
      try {
        console.log('Fetching audio URL for:', selectedSong.name);
        const response = await fetch(`http://localhost:3002/api/getAudioUrl/${encodeURIComponent(selectedSong.name)}`);
        // const response = await fetch(`http://localhost:3002/api/getImageUrl/${encodeURIComponent(selectedSong.name)}`);

        
        if (!response) {
          throw new Error(`Failed to fetch audio URL: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        console.log('Fetched audio data:', data);
        setAudioUrl(data.audioUrl);
      } catch (error) {
        console.error('Error fetching audio URL:', error);
      }
    };

    fetchAudioUrl();
  }, [selectedSong]);

  console.log(audioUrl);

  return (
    <div>
      {audioUrl ? (
        <audio key={audioUrl} controls>
          <source src={audioUrl} type="audio/flac" />
        </audio>
      ) : (
        <p>Loading audio...</p>
      )}
    </div>
  );
}

export default Footer;
