const LogInPage = ({ onToggle, isLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleAction = () => {
    if (isLogin) {
      // Simulating login logic
      if (username === 'user' && password === 'pass') {
        alert('Login successful!');
        setErrorMessage('');
        onToggle(); // Close the login popup on successful login
      } else {
        setErrorMessage('Invalid username or password');
      }
    } else {
      // Show the signup popup
      alert('Signup button clicked!');
      // Add your actual signup logic here

      // Optionally, you can close the signup popup after signup
      onToggle();
    }
  };

  return (
    <div className={`login-popup ${isLogin ? 'login' : 'signup'}`}>
      <form>
        <h2>{isLogin ? 'Login' : 'Signup'}</h2>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button type="button" onClick={handleAction}>
          {isLogin ? 'Login' : 'Signup'}
        </button>

        <p id="errorMessage">{errorMessage}</p>

        <p>
          {isLogin ? "Don't have an account? " : "Already have an account? "}
          <span className="toggle-link" onClick={onToggle}>
            {isLogin ? 'Signup here' : 'Login here'}
          </span>
        </p>
      </form>
    </div>
  );
};

export default LogInPage;
