// SearchPage.js
import React, { useState, useEffect } from 'react';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [songResults, setSongResults] = useState([]);
  const [albumResults, setAlbumResults] = useState([]);

  useEffect(() => {
    const debounce = (func, delay) => {
      let timeoutId;
      return function (...args) {
        if (timeoutId) {
          clearTimeout(timeoutId);
        }
        timeoutId = setTimeout(() => {
          func(...args);
        }, delay);
      };
    };

    const debouncedSearch = debounce(async (term) => {
      if (term.trim() === '') {
        setSongResults([]);
        setAlbumResults([]);
        return;
      }

      try {
        const response = await fetch(`http://localhost:3002/api/search?q=${encodeURIComponent(term)}`);
        const data = await response.json();

        setSongResults(data.filter((item) => item.type === 'song'));
        setAlbumResults(data.filter((item) => item.type === 'album'));
      } catch (error) {
        console.error(error);
      }
    }, 300);

    debouncedSearch(searchTerm);
  }, [searchTerm]);

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div>
        {/* Display song results */}
        {songResults.map((song) => (
          <div key={song.id}>
            <h3>{song.name}</h3>
            <p>{song.artist}</p>
          </div>
        ))}

        {/* Display album results */}
        {albumResults.map((album) => (
          <div key={album.id}>
            <h3>{album.name}</h3>
            <p>{album.artist}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchPage;
