import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const App = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);

  useEffect(() => {
    const audio = audioRef.current;

    audio.addEventListener('play', () => setIsPlaying(true));
    audio.addEventListener('pause', () => setIsPlaying(false));
    audio.addEventListener('timeupdate', () => setCurrentTime(audio.currentTime));
    audio.addEventListener('durationchange', () => setDuration(audio.duration));

    return () => {
      audio.removeEventListener('play', () => setIsPlaying(true));
      audio.removeEventListener('pause', () => setIsPlaying(false));
      audio.removeEventListener('timeupdate', () => setCurrentTime(audio.currentTime));
      audio.removeEventListener('durationchange', () => setDuration(audio.duration));
    };
  }, []);

  const togglePlayPause = () => {
    const audio = audioRef.current;
    if (audio.paused) {
      audio.play();
    } else {
      audio.pause();
    }
  };

  const handleVolumeChange = (event) => {
    const audio = audioRef.current;
    setVolume(event.target.value);
    audio.volume = event.target.value;
  };

  const calculateProgress = () => {
    return (currentTime / duration) * 100;
  };

  return (
    <div className="audio-player-container">
      <audio ref={audioRef} src="01-fka_twigs-thousand_eyes.flac"></audio>

      <div id="customControls">
        <button onClick={togglePlayPause}>
          {isPlaying ? 'Pause' : 'Play'}
        </button>
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={handleVolumeChange}
        />
        <progress value={calculateProgress()} max="100"></progress>
        <div className="progress-details">
          <span>Current Time: {currentTime.toFixed(2)}s</span>
          <span>Duration: {duration.toFixed(2)}s</span>
        </div>
      </div>
    </div>
  );
};

export default App;
