import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AlbumDetails = ({ setSelectedSong }) => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    // Fetch album details from the server
    const fetchAlbumDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3002/api/albums/${albumId}`);
        console.log('Album Details Response:', response); // Log the response for debugging
        if (response.status === 200) {
          setAlbum(response.data.album);
        }
      } catch (error) {
        console.error('An error occurred:', error);
      }
    };

    fetchAlbumDetails();
  }, [albumId]);

  const handleSongClick = (song) => {
    console.log(song);
    setSelectedSong(song);
  };

  const handleRemoveSong = async (songId) => {
    try {
      // Send a request to remove the song from the album on the server
      const response = await axios.delete(`http://localhost:3002/api/albums/${albumId}/removeSong/${songId}`);
      
      if (response.status === 200) {
        // Update the album on the client side after successful removal
        setAlbum(response.data.album);
        window.location.reload();
      }
    } catch (error) {
      console.error('An error occurred while removing the song:', error);
    }
  };

  return (
    <div className="album-details">
      <h2>Album Details</h2>
      {album ? (
        <div>
          <h3>{album.albumTitle}</h3>
          <p>Artist: {album.artist}</p>
          <p>Release Date: {album.releaseDate}</p>
          <p>Record Label: {album.recordLabel}</p>
          <img src={`images/${album.albumCoverArt}`} alt={album.albumTitle} style={{ maxWidth: '200px' }} />

          <h4>Tracks:</h4>
          <ul>
            {album.tracklist.map((track) => (
              <li key={track._id}>
                <strong>Title:</strong> {track.name}, <strong>Duration:</strong> {track.duration}
                <button onClick={() => handleRemoveSong(track._id)}>Remove</button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default AlbumDetails;
