import React, { useState } from 'react';
import OptionsPopup from './OptionsPopup';
import './SongResults.css';

function SongResults({ results, playSong }) {
  const [showOptions, setShowOptions] = useState(null);

  const handleShowOptions = (result) => {
    setShowOptions(result._id);
  };

  const handleCloseOptions = () => {
    setShowOptions(null);
  };

  const handleOption1 = () => {
    console.log("Option 1");
    // Implement your logic for Option 1
    handleCloseOptions();
  };

  const handleOption2 = () => {
    console.log("Option 2");
    // Implement your logic for Option 2
    handleCloseOptions();
  };

  const handleOption3 = () => {
    console.log("Option 3");
    // Implement your logic for Option 3
    handleCloseOptions();
  };

  return (
    <div>
      <ul className="song-container">
        {results.map((result) => (
          <li key={result._id} className="song-item">
            <span className="play-button" onClick={() => playSong(result)}>
              ▶️
            </span>
            <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
            {result.album}
            
            <span onClick={() => handleShowOptions(result)}>&#8942;</span>

            {/* Options popup for the selected song */}
            <div className="popup">
              <div className="popup-options">
                <div className="popup-option" onClick={handleOption1}>
                  Option 1
                </div>
                <div className="popup-option" onClick={handleOption2}>
                  Option 2
                </div>
                <div className="popup-option" onClick={handleOption3}>
                  Option 3
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SongResults;
