// App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Homepage from './HomePage'; 
import AlbumDetails from './AlbumDetails'; 
import Footer from './footer';
import Sidebar from './sidebar';
import SearchPage from './SearchPage';
import LogInPage from './LogInPage';

function App() {
  const [selectedSong, setSelectedSong] = useState(null);
  const [isLogin, setIsLogin] = useState(true);

  const handleToggle = () => {
    setIsLogin(!isLogin);
  };

  // Dummy authentication function, replace with your actual logic
  const handleLogin = (credentials) => {
    // Perform authentication logic here
    // For simplicity, considering login successful if username is 'user' and password is 'pass'
    const { username, password } = credentials;
    if (username === 'user' && password === 'pass') {
      setIsLogin(true);
    }
  };

  return (
    <Router>
      <div className="App">
        {!isLogin && <LogInPage onToggle={handleToggle} onLogin={handleLogin} />}

        {isLogin && (
          <div className="container">
            <Sidebar className="sidebar" />
            <div className="content">
              <Routes>
                <Route
                  path="/"
                  element={<Homepage />}
                />
                <Route
                  path="/album/:albumId"
                  element={<AlbumDetails setSelectedSong={setSelectedSong} />}
                />
                <Route
                  path="/search"
                  element={<SearchPage playSong={setSelectedSong} />}
                />
              </Routes>
            </div>
            <footer className="footer">
              {selectedSong && <Footer selectedSong={selectedSong} />}
            </footer>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
