// SearchPage.js
import React, { useState } from 'react';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState({ songs: [], albums: [] });

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:3002/api/search?q=${searchTerm}`);
      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <div>
        <h2>Song Results:</h2>
        <ul>
          {results.songs.map((result) => (
            <li key={result._id}>
              <strong>Title:</strong> {result.albumTitle}, <strong>Artist:</strong> {result.artist},{' '}
              <strong>Album:</strong> {result.album}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Album Results:</h2>
        <ul>
          {results.albums.map((result) => (
            <li key={result._id}>
              <strong>Title:</strong> {result.title}, <strong>Artist:</strong> {result.artist}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default SearchPage;
