// PlaylistDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PlaylistDetails = () => {
  const { playlistId } = useParams();
  const [playlist, setPlaylist] = useState(null);

  useEffect(() => {
    // Fetch playlist details from the server
    const fetchPlaylistDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3002/api/playlists/${playlistId}`);
        console.log('Playlist Details Response:', response); // Log the response for debugging
        if (response.status === 200) {
          const playlistData = response.data.playlist;

          // Fetch details for each track
          const tracksWithDetails = await Promise.all(
            playlistData.tracks.map(async (trackID) => {
              const trackResponse = await axios.get(`http://localhost:3002/api/tracks/${trackID}`);
              return trackResponse.data.track;
            })
          );

          setPlaylist({
            ...playlistData,
            tracks: tracksWithDetails,
          });
        }
      } catch (error) {
        console.error('An error occurred:', error);
      }
    };

    fetchPlaylistDetails();
  }, [playlistId]);

  console.log('Current Playlist:', playlist);

  return (
    <div className="playlist-details">
      <h2>Playlist Details</h2>
      {playlist ? (
        <div>
          <h3>{playlist.name}</h3>
          <p>Created on: {new Date(playlist.createdAt).toLocaleDateString()}</p>
          <p>Last Updated: {new Date(playlist.updatedAt).toLocaleDateString()}</p>
          <h4>Tracks:</h4>
          <ul>
            {playlist.tracks.map((track) => (
              <li key={track._id}>
                <strong>Title:</strong> {track.name}, <strong>Artist:</strong> {track.artist}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default PlaylistDetails;
