// AlbumDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AlbumDetails = ({ onSongClick }) => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    // Fetch album details based on albumId
    // ...

  }, [albumId]);

  const handleSongClick = (song) => {
    // Pass the selected song to the onSongClick handler
    onSongClick(song);
  };

  return (
    <div>
      {/* Display album details */}
      {/* ... */}
      <h3>Tracks:</h3>
      <ul>
        {album.tracklist.map(track => (
          // Pass the handleSongClick to each track in the list
          <li key={track._id} onClick={() => handleSongClick(track)}>
            {track.name} - {track.duration}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlbumDetails;
