// Sidebar.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import PlaylistCreation from './PlayListCreation';
import PlaylistDisplay from './PlaylistDisplay';

const Sidebar = () => {
  const [isCreatingPlaylist, setIsCreatingPlaylist] = useState(false);
  const navigate = useNavigate();

  const handleCreatePlaylist = () => {
    setIsCreatingPlaylist(true);
  };

  const handlePlaylistCreationComplete = () => {
    setIsCreatingPlaylist(false);
  };

  const handleOpenFavorites = () => {
    // Redirect to the Favorites page
    navigate('/favorites');
  };

  return (
    <div className="sidebar">
      <Link to="/">
        <div className="sidebar-link">Go to Homepage</div>
      </Link>
      <Link to="/search">
        <div className="sidebar-link">Go to Search Page</div>
      </Link>
      <div className="sidebar-link" onClick={handleOpenFavorites}>
        Favorites
      </div>
      <div className="sidebar-link" onClick={handleCreatePlaylist}>
        Create Playlist
      </div>
      {isCreatingPlaylist && <PlaylistCreation onCreatePlaylist={handlePlaylistCreationComplete} />}
      <div>
        <PlaylistDisplay />
      </div>
    </div>
  );
};

export default Sidebar;
