import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AlbumDetails = ({ setSelectedSong }) => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:3002/api/albums/${albumId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setAlbum(data))
      .catch(error => console.error(error));
  }, [albumId]);

  useEffect(() => {
    const audioElement = document.getElementById('audioPlayer');

    const handleAudioEnded = () => {
      // Play the next track when the current one ends
      playNextTrack();
    };

    if (audioElement) {
      audioElement.addEventListener('ended', handleAudioEnded);
    }

    return () => {
      // Remove the event listener when the component unmounts
      if (audioElement) {
        audioElement.removeEventListener('ended', handleAudioEnded);
      }
    };
  }, [currentTrackIndex, playNextTrack]);

  const playNextTrack = () => {
    const nextIndex = (currentTrackIndex + 1) % album.tracklist.length;
    setSelectedSong(album.tracklist[nextIndex]);
    setCurrentTrackIndex(nextIndex);
  };

  const handleSongClick = (track, index) => {
    setSelectedSong(track);
    setCurrentTrackIndex(index);
  };

  if (!album) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{album.albumTitle}</h2>
      <p>Artist: {album.artist}</p>
      <p>Release Date: {album.releaseDate}</p>
      <p>Record Label: {album.recordLabel}</p>
      <img src={`images/${album.albumCoverArt}`} alt={album.albumTitle} style={{ maxWidth: '200px' }} />

      <audio id="audioPlayer" controls>
        <source src={album.tracklist[currentTrackIndex].audioUrl} type="audio/mpeg" />
      </audio>

      <h3>Tracks:</h3>
      <ul>
        {album.tracklist.map((track, index) => (
          <li key={track._id} onClick={() => handleSongClick(track, index)}>
            {track.name} - {track.duration}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlbumDetails;
