import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Authentication = () => {
  const [isLoginPage, setIsLoginPage] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleToggle = () => {
    setIsLoginPage(!isLoginPage);
  };

  const handleAction = async () => {
    const endpoint = isLoginPage ? '/login' : '/signup';
    const data = {
      username: username,
      password: password,
    };

    try {
      const response = await fetch(`http://localhost:3002${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        // Login or signup successful
        console.log(`${isLoginPage ? 'Login' : 'Signup'} successful`);
        navigate('/'); // Redirect to the root route upon successful action
      } else {
        // Handle login or signup failure
        console.error(`Error during ${isLoginPage ? 'login' : 'signup'}`);
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  return (
    <div>
      <h2>{isLoginPage ? 'Login' : 'Signup'}</h2>
      <label htmlFor="username">Username:</label>
      <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <br />
      <label htmlFor="password">Password:</label>
      <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <br />
      <button onClick={handleAction}>{isLoginPage ? 'Login' : 'Signup'}</button>
      <br />
      <p>
        {isLoginPage ? "Don't have an account? " : "Already have an account? "}
        <span onClick={handleToggle}>{isLoginPage ? 'Signup here' : 'Login here'}</span>
      </p>
    </div>
  );
};

export default Authentication;
