import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import OptionsPopup from './OptionsPopup'; // Import the OptionsPopup component

function AlbumResults({ results, playlists }) {
  const navigate = useNavigate();
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [showPlaylists, setShowPlaylists] = useState(false);

  const handleAlbumClick = (albumId) => {
    navigate(`/album/${albumId}`);
  };

  const handleShowOptions = (album) => {
    setSelectedAlbum(album);
  };

  const handleCloseOptions = () => {
    setSelectedAlbum(null);
    setShowPlaylists(false);
  };

  const handleOption1 = (playlist) => {
    // Log both the playlist's ID and the selected album's ID
    console.log('Playlist ID:', playlist._id);
    console.log('Selected Album ID:', selectedAlbum._id);

    // Implement your logic for Option 1 (e.g., display playlists)
    setShowPlaylists(true);
    console.log("Option 1 - Display playlists");
  };

  const handleOption2 = () => {
    console.log("Option 2");
    // Implement your logic for Option 2
    handleCloseOptions();
  };

  const handleOption3 = () => {
    console.log("Option 3");
    // Implement your logic for Option 3
    handleCloseOptions();
  };

  const handlePlaylistClick = async (playlistId) => {
    try {
      // Send a request to add the album to the playlist on the server
      const response = await fetch(`http://localhost:3002/api/playlists/${playlistId}/addAlbum`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ albumId: selectedAlbum._id }),
      });

      if (response.ok) {
        console.log(`Album added to playlist ${playlistId}`);
      } else {
        console.error('Failed to add album to playlist:', response.status);
      }
    } catch (error) {
      console.error('An error occurred while adding album to playlist:', error);
    }
  };

  return (
    <div>
      <h2>Album Results</h2>
      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <div className="album-info">
              <strong>Album:</strong> {result.albumTitle}, <strong>Artist:</strong> {result.artist},{' '}
              <strong>Year:</strong> {result.year}
              <button onClick={() => handleAlbumClick(result._id)}>View Details</button>
              <button onClick={() => handleShowOptions(result)}>Add to Playlist</button>

              {/* Options popup for the selected album */}
              {selectedAlbum && selectedAlbum._id === result._id && (
                <OptionsPopup
                  playlists={playlists}
                  showPlaylists={showPlaylists}
                  handleOption1={handleOption1}
                  handleOption2={handleOption2}
                  handleOption3={handleOption3}
                  handleCloseOptions={handleCloseOptions}
                  handlePlaylistClick={handlePlaylistClick}
                  selectedAlbum={selectedAlbum}
                />
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AlbumResults;
