// SongResults.js
import React, { useState, useEffect } from 'react';

function SongResults({ results, playSong }) {
  const [showOptions, setShowOptions] = useState(null);
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');

        console.log(response)
        if (response.ok) {
          const data = await response.json();
          setPlaylists(data.playlists);
        } else {
          console.error('Failed to fetch playlists:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    // Fetch playlists when the component mounts
    fetchPlaylists();
  }, []); // Empty dependency array ensures it runs only once on mount

  const handleShowOptions = (resultId) => {
    setShowOptions(resultId);
  };

  const handleCloseOptions = () => {
    setShowOptions(null);
  };

  const handleOption1 = async (resultId) => {
    try {
      // Find the playlist by ID
      const playlist = playlists.find((pl) => pl._id === showOptions);

      if (!playlist) {
        console.error('Playlist not found');
        return;
      }

      // Update the tracklist in the playlist
      playlist.tracks.push(resultId);

      // Update the lastUpdated timestamp
      playlist.lastUpdated = Date.now();

      // Save the updated playlist
      const response = await fetch(`http://localhost:3002/api/playlists/${showOptions}/addTrack`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trackId: resultId,
        }),
      });

      if (response.ok) {
        console.log('Track added to playlist successfully');
      } else {
        console.error('Failed to add track to playlist:', response.status);
      }

      handleCloseOptions();
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  const handleOption2 = () => {
    console.log("Option 2");
    // Implement your logic for Option 2
  };

  const handleOption3 = () => {
    console.log("Option 3");
    // Implement your logic for Option 3
  };

  return (
    <div>
      {results.length > 0 && (
        <div>
          <h2>Song Results</h2>
          <ul>
            {results.map((result) => (
              <li key={result._id}>
                <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
                {result.album}
                <button onClick={() => handleShowOptions(result._id)}>More Options</button>

                {/* Options for the selected song */}
                {showOptions === result._id && (
                  <div>
                    <button onClick={() => handleOption1(result._id)}>Option 1</button>
                    <button onClick={handleOption2}>Option 2</button>
                    <button onClick={handleOption3}>Option 3</button>
                    <button onClick={handleCloseOptions}>Close Options</button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SongResults;
