// DisplaySongs.js
import React, { useEffect, useState } from 'react';

function DisplaySongs({ onSongClick }) {
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/getAllSongs');
        const data = await response.json();
        setSongs(data);
      } catch (error) {
        console.error('Error fetching songs:', error);
      }
    };

    fetchSongs();
  }, []);

  const handleSongClick = (song) => {
    onSongClick(song);
  };

  return (
    <div>
      <h2>All Songs</h2>
      {songs.length > 0 ? (
        <table>
        <thead>
          <tr>
            <th>Song</th>
            <th>Artist</th>
            <th>Album</th>
            <th>Duration</th>
          </tr>
        </thead>
        <tbody>
          {songs.map((song) => (
            <tr key={song._id} onClick={() => handleSongClick(song)}>
              <td>{song.name}</td>
              <td>{song.artist}</td>
              <td>{song.album}</td>
              <td>{song.duration}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
      ) : (
        <p>Loading songs...</p>
      )}
    </div>
  );
}

export default DisplaySongs;
