// Favorites.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Favorites.css';

function Favorites({ setSelectedSong }) {
  const [likedSongs, setLikedSongs] = useState([]);
  const [selectedSong, setSelectedSongOption] = useState(null);
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    const fetchLikedSongs = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/likedSongs');
        if (response.status === 200) {
          setLikedSongs(response.data.likedSongs);
        } else {
          console.error('Failed to fetch liked songs:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching liked songs:', error);
      }
    };

    // Fetch liked songs when the component mounts
    fetchLikedSongs();
  }, []);

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');
        if (response.ok) {
          const data = await response.json();
          setPlaylists(data.playlists);
        } else {
          console.error('Failed to fetch playlists:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    // Fetch playlists when the component mounts
    fetchPlaylists();
  }, []);

  const handlePlaySong = (song) => {
    setSelectedSong(song.songId); // Assuming song.songId is the correct ID
  };

  const handleShowOptions = (song) => {
    setSelectedSongOption(song);
  };

  const handlePlaylistClick = async (playlistId) => {
    try {
      const response = await fetch(`http://localhost:3002/api/playlists/${playlistId}/addSong`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ songId: selectedSong.songId }),
      });
  
      if (response.ok) {
        console.log(`Song added to playlist ${playlistId}`);
      } else {
        console.error('Failed to add song to playlist:', response.status);
      }
    } catch (error) {
      console.error('An error occurred while adding song to playlist:', error);
    }
  };

  return (
    <div>
      <h2>Favorites</h2>
      {likedSongs.length > 0 ? (
        <table className="favorites-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Artist</th>
              <th>Album</th>
              <th>Options</th>
            </tr>
          </thead>
          <tbody>
            {likedSongs.map((likedSong) => (
              <tr key={likedSong._id}>
                <td>{likedSong.songId.name}</td>
                <td>{likedSong.songId.artist}</td>
                <td>{likedSong.songId.album}</td>
                <td>
                  <button onClick={() => handlePlaySong(likedSong)}>Play</button>
                  <button onClick={() => handleShowOptions(likedSong)}>Details</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No liked songs yet.</p>
      )}

      {/* Display song details */}
      {selectedSong && (
        <div className="selected-song-details">
          <strong>Title:</strong> {selectedSong.songId.name},{' '}
          <strong>Artist:</strong> {selectedSong.songId.artist},{' '}
          <strong>Album:</strong> {selectedSong.songId.album}
        </div>
      )}
    </div>
  );
}

export default Favorites;
