// Sidebar.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PlaylistCreation from './PlayListCreation';
import PlaylistDisplay from './PlaylistDisplay';
import axios from 'axios';

const Sidebar = () => {
  const [isCreatingPlaylist, setIsCreatingPlaylist] = useState(false);
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    // Fetch playlists from the server
    const fetchPlaylists = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/playlists');
        if (response.status === 200) {
          setPlaylists(response.data.playlists);
        }
      } catch (error) {
        console.error('An error occurred:', error);
      }
    };

    fetchPlaylists();
  }, []); // Run the effect only once when the component mounts

  const handleCreatePlaylist = () => {
    setIsCreatingPlaylist(true);
  };

  const handlePlaylistCreationComplete = () => {
    setIsCreatingPlaylist(false);
    // Refresh playlists after creation
    fetchPlaylists();
  };

  return (
    <div className="sidebar">
      <h2>Sidebar Content</h2>
      <Link to="/">
        <button>Go to Homepage</button>
      </Link>
      <Link to="/search">
        <button>Go to Search Page</button>
      </Link>
      <button onClick={handleCreatePlaylist}>Create Playlist</button>
      {isCreatingPlaylist ? (
        <PlaylistCreation onCreatePlaylist={handlePlaylistCreationComplete} />
      ) : (
        <PlaylistDisplay playlists={playlists} />
      )}
    </div>
  );
};

export default Sidebar;
