import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './AlbumDetails.css';

const AlbumDetails = ({ setSelectedSong }) => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:3002/api/albums/${albumId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setAlbum(data))
      .catch(error => console.error(error));
  }, [albumId]);

  const playNextTrack = () => {
    const nextIndex = (currentTrackIndex + 1) % album.tracklist.length;
    setSelectedSong(album.tracklist[nextIndex]);
    setCurrentTrackIndex(nextIndex);
  };

  const handleSongClick = (track, index) => {
    console.log(track);
    setSelectedSong(track);
    setCurrentTrackIndex(index);
  };

  if (!album) {
    return <p>Loading...</p>;
  }

  return (
    <div className='album-details'>
      <h2 className='album-title'>{album.albumTitle}</h2>
      <p className='artist-info'>Artist: {album.artist}</p>
      <p className='release-info'>Release Date: {album.releaseDate}</p>
      <p className='label-info'>Record Label: {album.recordLabel}</p>
      <img src={`images/${album.albumCoverArt}`} className='album-cover' />
      <h3 className='tracks-heading'>Tracks:</h3>
      <table className='tracks-table'>
        <thead>
          <tr>
            <th className='track-name'>Name</th>
            <th className='track-duration'>Duration</th>
          </tr>
        </thead>
        <tbody>
          {album.tracklist.map((track, index) => (
            <tr key={track._id} onClick={() => handleSongClick(track, index)}>
              <td>{track.name}</td>
              <td>{track.duration}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* <button className='play-next-btn' onClick={playNextTrack}>Play Next Track</button> */}
    </div>
  );
};

export default AlbumDetails;
