// App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Homepage from './HomePage'; 
import AlbumDetails from './AlbumDetails'; 
import Footer from './footer';
import Sidebar from './sidebar';
import SearchPage from './SearchPage';
import LogInPage from './LogInPage';

function App() {
  const [selectedSong, setSelectedSong] = useState(null);
  const [isLogin, setIsLogin] = useState(true);

  const handleToggle = () => {
    setIsLogin(!isLogin);
  };

  // Dummy authentication function, replace with your actual logic
  const handleLogin = (credentials) => {
    // Perform authentication logic here
    // For simplicity, considering login successful if username is 'user' and password is 'pass'
    const { username, password } = credentials;
    if (username === 'user' && password === 'pass') {
      setIsLogin(false);
    }
  };

  // Render the rest of the content if logged in
  if (isLogin) {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Sidebar className="sidebar" />
            <div className="content">
              <Routes>
                <Route
                  path="/"
                  element={<Homepage />}
                />
                <Route
                  path="/album/:albumId"
                  element={<AlbumDetails setSelectedSong={setSelectedSong} />}
                />
                <Route
                  path="/search"
                  element={<SearchPage playSong={setSelectedSong} />}
                />
              </Routes>
            </div>
          </div>
          <footer className="footer">
            {selectedSong && <Footer selectedSong={selectedSong} />}
          </footer>
        </div>
      </Router>
    );
  }

  // Redirect to login page if not logged in
  return <LogInPage onToggle={handleToggle} onLogin={handleLogin} />;
}

export default App;
