import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AlbumDetails = ({ onSongClick }) => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3002/api/albums/${albumId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setAlbum(data))
      .catch(error => console.error(error));
  }, [albumId]);

  if (!album) {
    return <p>Loading...</p>;
  }

  const handleSongClick = (track) => {
    onSongClick(track);
  };

  return (
    <div>
      <h2>{album.albumTitle}</h2>
      <p>Artist: {album.artist}</p>
      <p>Release Date: {album.releaseDate}</p>
      <p>Record Label: {album.recordLabel}</p>
      <img src={`images/${album.albumCoverArt}`} alt={album.albumTitle} style={{ maxWidth: '200px' }} />
      <h3>Tracks:</h3>
      <ul>
        {album.tracklist.map(track => (
          <li key={track._id} onClick={() => handleSongClick(track)}>
            {track.name} - {track.duration}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlbumDetails;
