import React, { useEffect, useState } from 'react';

function App() {
  const [audioUrl, setAudioUrl] = useState('');

  useEffect(() => {
    // Fetch the audio file URL from the server when the component mounts
    const fetchAudioUrl = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/getAudioUrl'); // Replace with your actual server endpoint
        const data = await response.json();
        setAudioUrl(data.audioUrl);
      } catch (error) {
        console.error('Error fetching audio URL:', error);
      }
    };

    fetchAudioUrl();
  }, []); // The empty dependency array ensures that the effect runs only once when the component mounts

  return (
    <div className="App">
      {audioUrl ? (
        <audio controls>
          <source src={audioUrl} type="audio/flac" />
          Your browser does not support the audio element.
        </audio>
      ) : (
        <p>Loading audio...</p>
      )}
    </div>
  );
}

export default App;
