import React, { useState, useEffect } from 'react';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);

  // Debounce function to delay the search request
  const debounce = (func, delay) => {
    let timeoutId;
    return function (...args) {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      timeoutId = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  // Perform the search when the searchTerm changes, debounced with a delay of 300 milliseconds
  const debouncedSearch = debounce(async (term) => {
    if (term.trim() === '') {
      setResults([]); // Clear results if the search term is empty
      return;
    }

    try {
      const response = await fetch(`http://localhost:3002/api/search?q=${encodeURIComponent(term)}`);
      const data = await response.json();
      console.log(data);
      setResults(data);
    } catch (error) {
      console.error(error);
    }
  }, 300);

  // useEffect to trigger the debounced search when searchTerm changes
  useEffect(() => {
    debouncedSearch(searchTerm);
  }, [searchTerm, debouncedSearch]);

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <strong>Title:</strong> {result.name}, <strong>Artist:</strong> {result.artist}, <strong>Album:</strong>{' '}
            {result.album}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SearchPage;
