// SearchPage.js
import React, { useState } from 'react';
import SongResults from './SongResults';
import AlbumResults from './AlbumResults';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState({ songs: [], albums: [] });

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:3002/api/search?q=${searchTerm}`);
      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {/* <SongResults songs={results.songs} /> */}
      <AlbumResults albums={results.albums} />
    </div>
  );
}

export default SearchPage;
