// AlbumDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AlbumDetails = () => {
  const { albumId } = useParams();
  const [album, setAlbum] = useState(null);

  useEffect(() => {
    // Fetch album details based on albumId
    fetch(`http://localhost:3002/api/albums/${albumId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setAlbum(data))
      .catch(error => console.error(error));
  }, [albumId]);

  if (!album) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{album.albumTitle}</h2>
      <p>Artist: {album.artist}</p>
      <p>Release Date: {album.releaseDate}</p>
      <p>Genre: {album.genre ? album.genre.join(', ') : 'N/A'}</p>
      <p>Record Label: {album.recordLabel}</p>
      <img
        src={`images/${album.albumCoverArt || 'placeholder-url.jpg'}`}
        alt={`${album.albumTitle} Cover`}
        className="cover-art"
      />
      <h3>Tracklist:</h3>
      <table>
        <thead>
          <tr>
            <th>Song Name</th>
            <th>Artist</th>
            <th>Album</th>
            <th>Genre</th>
            <th>Duration</th>
            <th>Listen to Song</th>
            <th>Cover Art</th>
          </tr>
        </thead>
        <tbody>
          {album.tracklist.map(song => (
            <tr key={song._id}>
              <td>{song.albumTitle}</td>
              <td>{song.artist}</td>
              <td>{song.album}</td>
              <td>{song.duration}</td>
              <td>
                <a href={song.songURL} target="_blank" rel="noopener noreferrer">
                  Listen
                </a>
              </td>
              <td>
                <img
                  src={`images/${song.imageURL || 'placeholder-url.jpg'}`}
                  alt={`${song.name} Cover`}
                  className="cover-art"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AlbumDetails;
