import React, { useState } from 'react';
import OptionsPopup from './OptionsPopup';
import { Link, useNavigate } from 'react-router-dom';

function AlbumResults({ results }) {
  const navigate = useNavigate();
  const [showOptions, setShowOptions] = useState(null);

  const handleAlbumClick = (albumId) => {
    navigate(`/album/${albumId}`);
  };

  const handleShowOptions = (albumId) => {
    setShowOptions(albumId);
  };

  const handleCloseOptions = () => {
    setShowOptions(null);
  };

  const handleOption1 = () => {
    console.log('Option 1');
    handleCloseOptions();
  };

  const handleOption2 = () => {
    console.log('Option 2');
    handleCloseOptions();
  };

  const handleOption3 = () => {
    console.log('Option 3');
    handleCloseOptions();
  };

  return (
    <div>
      <h2>Album Results</h2>
      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <div className="album-link">
              <strong>Album:</strong> {result.albumTitle}, <strong>Artist:</strong> {result.artist},{' '}
              <strong>Year:</strong> {result.year}
              <span onClick={() => handleAlbumClick(result._id)}>View Album</span>
              <button onClick={() => handleShowOptions(result._id)}>Options</button>

              {/* Options popup for the selected album */}
              {showOptions === result._id && (
                <OptionsPopup
                  handleOption1={handleOption1}
                  handleOption2={handleOption2}
                  handleOption3={handleOption3}
                  handleCloseOptions={handleCloseOptions}
                />
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AlbumResults;
