// Favorites.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import OptionsPopup from './OptionsPopup';

function Favorites({ setSelectedSong }) {
  const [likedSongs, setLikedSongs] = useState([]);
  const [selectedSongOption, setSelectedSongOption] = useState(null);
  const [showOptions, setShowOptions] = useState(false);

  useEffect(() => {
    const fetchLikedSongs = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/likedSongs');
        if (response.status === 200) {
          setLikedSongs(response.data.likedSongs);
        } else {
          console.error('Failed to fetch liked songs:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching liked songs:', error);
      }
    };

    // Fetch liked songs when the component mounts
    fetchLikedSongs();
  }, []);

  const handlePlaySong = (song) => {
    setSelectedSong(song.songId); // Assuming song.songId is the correct ID
  };

  const handleShowOptions = (song) => {
    setSelectedSongOption(song);
    setShowOptions(true);
  };

  const handleCloseOptions = () => {
    setSelectedSongOption(null);
    setShowOptions(false);
  };

  const handleOption1 = (playlist) => {
    // Log both the playlist's ID and the selected song's ID
    console.log('Playlist ID:', playlist._id);
    console.log('Selected Song ID:', selectedSongOption.songId);
  
    // Implement your logic for Option 1
    setShowOptions(false);
    console.log("Option 1 - Display playlists");
  };

  const handleOption2 = async () => {
    try {
      // Send a request to add the song to the LikedSongs collection
      const response = await axios.post('http://localhost:3002/api/likedSongs/addSong', {
        songId: selectedSongOption.songId,
      });

      if (response.status === 200) {
        console.log('Song added to LikedSongs');
      } else {
        console.error('Failed to add song to LikedSongs:', response.status);
      }

      // Close the options popup
      handleCloseOptions();
    } catch (error) {
      console.error('An error occurred while adding song to LikedSongs:', error);
    }
  };

  const handleOption3 = () => {
    console.log("Option 3");
    // Implement your logic for Option 3
    handleCloseOptions();
  };

  return (
    <div>
      <h2>Favorites</h2>
      {likedSongs.length > 0 ? (
        <ul>
          {likedSongs.map((likedSong) => (
            <li key={likedSong._id}>
              {/* Display song details */}
              <strong>Title:</strong> {likedSong.songId.name}, <strong>Artist:</strong> {likedSong.songId.artist},{' '}
              <strong>Album:</strong> {likedSong.songId.album}
              <button onClick={() => handlePlaySong(likedSong)}>Play</button>
              <button onClick={() => handleShowOptions(likedSong)}>More Details</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No liked songs yet.</p>
      )}

      {/* Options popup for the selected song */}
      {selectedSongOption && (
        <OptionsPopup
          playlists={[]}  // Pass appropriate playlists if needed
          showPlaylists={false}  // Pass appropriate value
          handleOption1={handleOption1}
          handleOption2={handleOption2}
          handleOption3={handleOption3}
          handleCloseOptions={handleCloseOptions}
          selectedSong={selectedSongOption}
        />
      )}
    </div>
  );
}

export default Favorites;
