// SearchPage.js
import React, { useState } from 'react';
import SearchResult from './SearchResult'; // Create SearchResult component to display search results

const SearchPage = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    // Perform the search based on the query and update the results
    try {
      const response = await fetch(`http://localhost:3002/api/search?query=${encodeURIComponent(query)}`);
      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <div>
      <h2>Search Page</h2>
      <input
        type="text"
        placeholder="Search..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      {/* Display search results */}
      <SearchResult results={results} />
    </div>
  );
};

export default SearchPage;
