// RecentlyPlayed.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function RecentlyPlayed({ setSelectedSong }) {
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);

  useEffect(() => {
    const fetchRecentlyPlayed = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/recentlyplayed');
        if (response.status === 200) {
          const reversedRecentlyPlayed = response.data.recentlyPlayed.slice(0, 8).reverse();
          setRecentlyPlayed(reversedRecentlyPlayed);
        } else {
          console.error('Failed to fetch recently played songs:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching recently played songs:', error);
      }
    };

    // Fetch recently played songs when the component mounts
    fetchRecentlyPlayed();
  }, []);

  const handleSongClick = (song) => {
    console.log(song);
    setSelectedSong(song);
  };

  return (
    <div>
      {recentlyPlayed.length > 0 ? (
        <table>
          <tbody>
            {recentlyPlayed.map((recentlyPlayedItem) => (
              <tr key={recentlyPlayedItem._id} onClick={() => handleSongClick(recentlyPlayedItem)}>
                <td>{recentlyPlayedItem.songId.name}</td>
                <td>{recentlyPlayedItem.songId.artist}</td>
                <td>{recentlyPlayedItem.songId.album}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No recently played songs yet.</p>
      )}
    </div>
  );
}

export default RecentlyPlayed;
