import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PlaylistDetails = ({ setSelectedSong }) => {
  const { playlistId } = useParams();
  const [playlist, setPlaylist] = useState(null);

  useEffect(() => {
    // Fetch playlist details from the server
    const fetchPlaylistDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3002/api/playlists/${playlistId}`);
        console.log('Playlist Details Response:', response); // Log the response for debugging
        if (response.status === 200) {
          setPlaylist(response.data.playlist);
        }
      } catch (error) {
        console.error('An error occurred:', error);
      }
    };

    fetchPlaylistDetails();
  }, [playlistId]);

  const handleSongClick = (song) => {
    console.log(song);
    setSelectedSong(song);
  };

  const handleRemoveSong = async (songId) => {
    try {
      // Send a request to remove the song from the playlist on the server
      const response = await axios.delete(`http://localhost:3002/api/playlists/${playlistId}/removeSong/${songId}`);
      
      if (response.status === 200) {
        // Update the playlist on the client side after successful removal
        setPlaylist(response.data.playlist);
      }
    } catch (error) {
      console.error('An error occurred while removing the song:', error);
    }
  };

  return (
    <div className="playlist-details">
      <h2>Playlist Details</h2>
      {playlist ? (
        <div>
          <h3>{playlist.name}</h3>
          <p>Created on: {new Date(playlist.createdAt).toLocaleDateString()}</p>
          <p>Last Updated: {new Date(playlist.updatedAt).toLocaleDateString()}</p>
          <h4>Tracks:</h4>
          <ul>
            {playlist.tracks.map((track) => (
              <li key={track._id}>
                <strong>Title:</strong> {track.name}, <strong>Artist:</strong> {track.artist}
                <button onClick={() => handleRemoveSong(track._id)}>Remove</button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default PlaylistDetails;
