// OptionsPopup.js
import React from 'react';

function OptionsPopup({ playlists, showPlaylists, handleOption1, handleOption2, handleOption3, handleCloseOptions, selectedSong }) {
  return (
    <div className="popup">
      {/* Options for the selected song */}
      {showPlaylists ? (
        <div>
          <h3>Select a Playlist</h3>
          <ul>
            {playlists.map((playlist) => (
              <li key={playlist._id} onClick={() => handleOption1(playlist)}>
                {playlist.name}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <button onClick={handleOption1}>Option 1</button>
          <button onClick={handleOption2}>Option 2</button>
          <button onClick={handleOption3}>Option 3</button>
        </div>
      )}
      <button onClick={handleCloseOptions}>Close Options</button>
    </div>
  );
}

export default OptionsPopup;
