import React from 'react';
import { Link, useHistory } from 'react-router-dom';

function AlbumResults({ results }) {
  const history = useHistory();

  const handleAlbumClick = (albumId) => {
    // Redirect to the AlbumDetails page
    history.push(`/album/${albumId}`);
  };

  if (results.length === 0) {
    // If there are no results, return null (component won't render)
    return null;
  }

  return (
    <div>
      <h2>Album Results</h2>
      <ul>
        {results.map((result) => (
          <li key={result._id}>
            {/* Use a function to handle the click event */}
            <div onClick={() => handleAlbumClick(result._id)} className="album-link">
              <strong>Album:</strong> {result.albumTitle}, <strong>Artist:</strong> {result.artist},{' '}
              <strong>Year:</strong> {result.year}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AlbumResults;
