// Favorites.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Favorites() {
  const [likedSongs, setLikedSongs] = useState([]);

  useEffect(() => {
    const fetchLikedSongs = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/likedSongs/populated');
        if (response.status === 200) {
          setLikedSongs(response.data.likedSongs);
        } else {
          console.error('Failed to fetch liked songs:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching liked songs:', error);
      }
    };

    // Fetch liked songs when the component mounts
    fetchLikedSongs();
  }, []);

  return (
    <div>
      <h2>Favorites</h2>
      {likedSongs.length > 0 ? (
        <ul>
          {likedSongs.map((likedSong) => (
            <li key={likedSong._id}>
              {/* Display song details */}
              <strong>Title:</strong> {likedSong.song.title}, <strong>Artist:</strong> {likedSong.song.artist},{' '}
              <strong>Album:</strong> {likedSong.song.album}
            </li>
          ))}
        </ul>
      ) : (
        <p>No liked songs yet.</p>
      )}
    </div>
  );
}

export default Favorites;
