// SearchPage.js
import React, { useState, useEffect } from 'react';
import SongResults from './SongResults';
import AlbumResults from './AlbumResults';

function SearchPage({ playSong }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [songResults, setSongResults] = useState([]);
  const [albumResults, setAlbumResults] = useState([]);
  const [selectedSong, setSelectedSong] = useState(null); // Track the selected song for details
  const [isMoreDetailsOpen, setIsMoreDetailsOpen] = useState(false); // Track if the "More Details" section is open
  const [playlists, setPlaylists] = useState([]); // Track playlists from the collection
  const [selectedPlaylist, setSelectedPlaylist] = useState(null); // Track the selected playlist for Option 1

  // Fetch the list of playlists when the component mounts
  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/playlists');
        const data = await response.json();
        console.log(data);
        setPlaylists(data.playlists);
      } catch (error) {
        console.error('An error occurred while fetching playlists:', error);
      }
    };

    fetchPlaylists();
  }, []);

  // Debounce function to delay the search request
  const debounce = (func, delay) => {
    let timeoutId;
    return function (...args) {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      timeoutId = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  // Perform the search when the searchTerm changes, debounced with a delay of 300 milliseconds
  const debouncedSearch = debounce(async (term) => {
    if (term.trim() === '') {
      setSongResults([]); // Clear song results if the search term is empty
      setAlbumResults([]); // Clear album results if the search term is empty
      return;
    }

    try {
      const response = await fetch(`http://localhost:3002/api/search?q=${encodeURIComponent(term)}`);
      const data = await response.json();
      console.log(data);

      // Assuming data is an array of results containing both songs and albums
      setSongResults(data.filter((item) => item.type === 'song'));
      setAlbumResults(data.filter((item) => item.type === 'album'));
    } catch (error) {
      console.error(error);
    }
  }, 300);

  // useEffect to trigger the debounced search when searchTerm changes
  useEffect(() => {
    debouncedSearch(searchTerm);
  }, [searchTerm, debouncedSearch]);

  // Handler to open the "More Details" section for a selected song
  const handleOpenDetails = (song) => {
    setSelectedSong(song);
    setIsMoreDetailsOpen(true);
  };

  // Handler to close the "More Details" section
  const handleCloseDetails = () => {
    setSelectedSong(null);
    setIsMoreDetailsOpen(false);
  };

  // Handler to select a playlist for Option 1
  const handleSelectPlaylist = (playlist) => {
    setSelectedPlaylist(playlist);
  };

  return (
    <div>
      <h1>Music Search</h1>
      <input
        type="text"
        placeholder="Search for songs or albums"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Display song results with details and options */}
      <div>
        {songResults.map((song) => (
          <div key={song.id}>
            <h3>{song.name}</h3>
            <p>{song.artist}</p>
            {/* Add other song details here */}
            <button onClick={() => handleOpenDetails(song)}>More Details</button>
            {isMoreDetailsOpen && selectedSong && selectedSong.id === song.id && (
              <div>
                {/* Display options for the selected song */}
                <button onClick={handleSelectPlaylist}>Option 1</button>
                {selectedPlaylist && (
                  <div>
                    {/* Display selected playlist details */}
                    <h4>Selected Playlist: {selectedPlaylist.name}</h4>
                    {/* Add other playlist details here */}
                  </div>
                )}
                <button onClick={handleCloseDetails}>Close Details</button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Use the AlbumResults component for albums */}
      <AlbumResults results={albumResults} />
    </div>
  );
}

export default SearchPage;
