import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Footer({ selectedSong, playlist, setPlaylist, currentSongIndex, setCurrentSongIndex }) {
  const [audioUrl, setAudioUrl] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    const fetchMediaData = async () => {
      try {
        console.log('Fetching audio and image URLs for:', selectedSong.name);

        // Fetch audio URL using axios
        const audioResponse = await axios.get(`http://localhost:3002/api/getAudioUrl/${encodeURIComponent(selectedSong.name)}`);
        const audioData = audioResponse.data;
        console.log('Fetched audio data:', audioData);
        setAudioUrl(audioData.audioUrl);

        // Fetch image URL using axios
        const imageResponse = await axios.get(`http://localhost:3002/api/getImageUrl/${encodeURIComponent(selectedSong.name)}`);
        const imageData = imageResponse.data;
        console.log('Fetched image data:', imageData);
        setImageUrl(imageData.imageUrl);

        // Add the recently played song to the collection
        await axios.post('http://localhost:3002/api/recentlyPlayed', {
          songId: selectedSong._id,
          // Add other relevant song details if needed
        });
      } catch (error) {
        console.error('Error fetching media data:', error);
      }
    };

    if (selectedSong) {
      fetchMediaData();
    }
  }, [selectedSong]);

  const fakeAudioUrl = 'https://example.com/fake-audio.mp3';

  const handleAudioEnded = () => {
    // Logic to play the next song after the current one ends
    const nextIndex = (currentSongIndex + 1) % playlist.length;
    setAudioUrl(''); // Reset audio URL to trigger re-render and load the new audio
    setCurrentSongIndex(nextIndex);
  };

  return (
    <div>
      {selectedSong ? (
        <div>
          {audioUrl ? (
            <div>
              <audio key={audioUrl} controls onEnded={handleAudioEnded}>
                <source src={audioUrl} type="audio/flac" />
              </audio>
              {imageUrl && <img src={imageUrl} alt="Song Artwork" />}
            </div>
          ) : (
            <p>Loading audio...</p>
          )}
        </div>
      ) : (
        <div>
          <div className="fake-audio-player">
            <audio controls>
              <source src={fakeAudioUrl} type="audio/flac" />
            </audio>
          </div>
        </div>
      )}
    </div>
  );
}

export default Footer;
