// Favorites.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Favorites() {
  const [likedSongs, setLikedSongs] = useState([]);

  useEffect(() => {
    const fetchLikedSongs = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/likedSongs');
        if (response.status === 200) {
          setLikedSongs(response.data.likedSongs);
        } else {
          console.error('Failed to fetch liked songs:', response.status);
        }
      } catch (error) {
        console.error('An error occurred while fetching liked songs:', error);
      }
    };

    // Fetch liked songs when the component mounts
    fetchLikedSongs();
  }, []);

  const fetchSongDetails = async (songId) => {
    try {
      const response = await axios.get(`http://localhost:3002/api/songs/${songId}`);
      if (response.status === 200) {
        return response.data.song; // Assuming your backend returns details of the song
      } else {
        console.error(`Failed to fetch details for song ${songId}:`, response.status);
        return null;
      }
    } catch (error) {
      console.error(`An error occurred while fetching details for song ${songId}:`, error);
      return null;
    }
  };

  const [songDetails, setSongDetails] = useState({});

  useEffect(() => {
    const fetchDetails = async () => {
      const detailsPromises = likedSongs.map((likedSong) => fetchSongDetails(likedSong.songId));
      const details = await Promise.all(detailsPromises);
      setSongDetails(details);
    };

    fetchDetails();
  }, [likedSongs]);

  return (
    <div>
      <h2>Favorites</h2>
      {likedSongs.length > 0 ? (
        <ul>
          {songDetails.map((details, index) => (
            <li key={likedSongs[index]._id}>
              {/* Display song details */}
              <strong>Title:</strong> {details.title}, <strong>Artist:</strong> {details.artist},{' '}
              <strong>Album:</strong> {details.album}
            </li>
          ))}
        </ul>
      ) : (
        <p>No liked songs yet.</p>
      )}
    </div>
  );
}

export default Favorites;
