import React, { useEffect, useState } from 'react';
import './footer.css';

function Footer() {
  const [songData, setSongData] = useState({
    songUrl: '',
    imageUrl: '',
  });

  useEffect(() => {
    const fetchSongData = async () => {
      try {
        const response = await fetch('http://localhost:3002/api/getSongData');
        const data = await response.json();
        setSongData(data);
      } catch (error) {
        console.error('Error fetching song data:', error);
      }
    };

    fetchSongData();
  }, []);

  return (
    <div className="App">
      {songData.songUrl ? (
        <div>
          <audio controls>
            <source src={songData.songUrl} type="audio/flac" />
            Your browser does not support the audio element.
          </audio>
          <img src={songData.imageUrl} alt="Song Cover" style={{ maxWidth: '200px' }} />
        </div>
      ) : (
        <p>Loading song data...</p>
      )}
    </div>
  );
}

export default Footer;
